import React, { useState } from "react";
import "./About.css";

const About = (props) => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`about-container ${darkMode ? 'dark' : 'light'}`}>
      <div className="about-box">
        <h1>About TextUtils</h1>
        <p>
          TextUtils is a powerful and user-friendly utility tool to manipulate and analyze text efficiently.
          Whether you're a student, developer, or writer, this app provides fast and effective text transformations.
        </p>

        <h2>‚ú® Features</h2>
        <ul className="features-list">
          <li>üî† Convert text to <strong>Uppercase</strong></li>
          <li>üî° Convert text to <strong>Lowercase</strong></li>
          <li>üßπ <strong>Clear</strong> the entire <strong>text</strong></li>
          <li>üìù Convert to <strong>Title Case</strong></li>
          <li>üö´ <strong>Remove</strong> extra <strong>spaces</strong></li>
          <li>üìã <strong>Copy</strong> text to <strong>clipboard</strong></li>
          <li>üî¢ <strong>Count</strong> characters and <strong>words</strong></li>
        </ul>

        <p>
          Designed with simplicity in mind, TextUtils helps you process text quickly without distractions.
        </p>

        <p>
          Made with ‚ù§Ô∏è using <strong>React</strong>, <strong>HTML</strong>, <strong>CSS</strong> & modern design principles.
        </p>

        <button onClick={toggleDarkMode} className="toggle-button">
          Toggle {darkMode ? 'Light' : 'Dark'} Mode
        </button>
      </div>
    </div>
  );
};

export default About;
