import React from 'react'

export default function Login() {
    const togglePassword = document.querySelector('.toggle-password');
    const passwordInput = document.getElementById('password');

    togglePassword.addEventListener('click', () => {
        // Toggle the type attribute
        const type = passwordInput.getAttribute('type') === 'password' ? 'text' : 'password';
        passwordInput.setAttribute('type', type);

        // Toggle the eye icon
        togglePassword.textContent = type === 'password' ? 'üëÅÔ∏è' : 'üôà';
    });

    // Form Validation
    const loginForm = document.getElementById('loginForm');
    const usernameInput = document.getElementById('username');
    

    loginForm.addEventListener('submit', (event) => {
        // Prevent form submission
        event.preventDefault();

        const username = usernameInput.value.trim();
        const password = passwordInput.value.trim();

        if (username === '' || password === '') {
            alert('Please fill in both username and password.');
        } else {
            // Proceed with login logic
            alert(`Welcome, ${username}!`);
            // In a real app, you'd handle login here (e.g., send to server)
        }
    });

    return (

        <>


            <div class="login-container">
                <div class="left-section">
                    <h1>Quiz Master</h1>
                    <p>Your gateway to endless quizzes!</p>
                    <img src="/Assets/26161029_4ra5_kd3y_220310.jpg" alt="Quiz Illustration" />
                </div>
                <div class="right-section">
                    <h2>Login</h2>
                    <button class="social-login google">Login with Google</button>
                    <button class="social-login linkedin">Login with LinkedIn</button>
                    <div class="divider">OR</div>
                    <form id="loginForm">
                        <div class="input-container">
                            <input type="text" id="username" placeholder="Username or email address" />
                            <span class="icon">üë§</span>
                        </div>
                        <div class="input-container">
                            <input type="password" id="password" placeholder="Password" />
                            <span class="toggle-password">üëÅÔ∏è</span>
                        </div>
                        <p><a href="#" id="forgotPassword" class="small-text">I can't remember my login ID</a></p>
                        <button type="submit" class="login-btn">Continue</button>
                    </form>

                </div>


            </div>
        </>

    )
}
