import React, { useState, useRef } from 'react';
import "./Textform.css";
export default function TextForm(props) {
    const [searchTerm, setSearchTerm] = useState("");
    const [searchCount, setSearchCount] = useState(0);

    const handleSearch = (e) => {
        e.preventDefault();
        const index = text.toLowerCase().indexOf(searchTerm.toLowerCase());
        if (index === -1) {
            props.showAlert(`No match found for "${searchTerm}"`, "warning");
            return;
        }

        // Scroll to match
        textRef.current.focus();
        textRef.current.setSelectionRange(index, index + searchTerm.length);
        props.showAlert(`Found 1st match for "${searchTerm}"`, "success");
    };

    const [text, setText] = useState("Enter text hereðŸ«¡");
    const textRef = useRef();

    const handleUpCLick = () => {
        let newText = text.toUpperCase();
        setText(newText);
        props.showAlert("Converted to UpperCase", 'success');
    };

    const handledownCLick = () => {
        let newText = text.toLowerCase();
        setText(newText);
        props.showAlert("Converted to lowerCase", 'success');
    };

    const HandleONchange = (event) => {
        setText(event.target.value);
    };

    const handleClear = () => {
        setText('');
        props.showAlert("Text cleared!", 'success');
    };

    const toTitleCase = (text) => {
        return text
            .split("\n")
            .map(line =>
                line
                    .toLowerCase()
                    .split(" ")
                    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
                    .join(" ")
            )
            .join("\n");
    };

    const handleTitleClick = () => {
        let newText = toTitleCase(text);
        setText(newText);
        props.showAlert("Converted to TitleCase", 'success');
    };

    const handleRemoveSpaces = () => {
        let newText = text.split(/\s+/).join(' ').trim();
        setText(newText);
        props.showAlert("Extra spaces removed", "success");
    };

    const handleCopy = () => {
        textRef.current.select();
        navigator.clipboard.writeText(textRef.current.value);
        props.showAlert("Copied to clipboard!", "success");
    };

    const countWordsAndCharacters = (text) => {
        const trimmedText = text.trim();
        const wordCount = trimmedText === "" ? 0 : trimmedText.split(/\s+/).length;
        const charCount = trimmedText.length;
        return { wordCount, charCount };
    };

    const { wordCount, charCount } = countWordsAndCharacters(text);

    return (
        <>
            <div className="container" >
                <form className="d-flex my-3" onSubmit={handleSearch}>
                    <input
                        className="form-control me-2"
                        type="search"
                        placeholder="Fiand text..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <button className="btn btn-outline-success" type="submit">Search</button>
                </form>
                {searchCount > 0 && (
                    <div className="alert alert-info my-2">
                        {searchCount} result(s) found for "{searchTerm}"
                    </div>
                )}

                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea
                        ref={textRef}
                        className="form-control"
                        value={text}
                        onChange={HandleONchange}
                        id="MYBOX"
                        rows="8"

                    ></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpCLick}>Uppercase</button>
                <button className="btn btn-primary mx-2" onClick={handledownCLick}>Lowercase</button>
                <button className="btn btn-primary mx-2" onClick={handleClear}>Clear</button>
                <button className="btn btn-primary mx-2" onClick={handleTitleClick}>Title Case</button>
                <button className="btn btn-primary mx-2" onClick={handleRemoveSpaces}>Remove Extra Spaces</button>
                <button className="btn btn-primary mx-2" onClick={handleCopy}>Copy to Clipboard</button>
            </div>

            <div className="container my-3">
                <h3>Your text summary</h3>
                <p>{wordCount} words and {charCount} characters</p>
                <p>{(0.008 * wordCount).toFixed(2)} minutes read</p>
                <h3>Preview</h3>
                <p>{text.length > 0 ? text : "Enter something to preview"}</p>
            </div>



        </>
    );
}
