import React from 'react';
import { NavLink } from 'react-router-dom'; // Import NavLink instead of Link
import propTypes from 'prop-types';

export default function Navbar(props) {
      const [searchTerm, setSearchTerm] = useState('');
      const [searchCount, setSearchCount] = useState(0);
      const [matches, setMatches] = useState([]); // To store all match details
      const [currentMatch, setCurrentMatch] = useState(0); // To track current match index
      const textRef = useRef();
      // Function to handle search and counting matches
      const handleSearch = (e) => {
          e.preventDefault();
          if (!searchTerm.trim()) {
              props.showAlert("Please enter something to search.", "warning");
              return;
          }
  
          const regex = new RegExp(searchTerm, "gi");
          const matches = [...text.matchAll(regex)]; // Get all matches with indices
  
          if (matches.length === 0) {
              props.showAlert(`No match found for "${searchTerm}"`, "warning");
              return;
          }
  
          setMatches(matches); // Store all matches with their indices
          setSearchCount(matches.length); // Set search count
          props.showAlert(`${matches.length} match(es) found for "${searchTerm}"`, "success");
  
          // Optionally scroll to the first match
          textRef.current.setSelectionRange(matches[0].index, matches[0].index + searchTerm.length);
          textRef.current.focus();
      };
  
      // Function to go to the next match
      const goToNextMatch = () => {
          if (currentMatch < matches.length - 1) {
              setCurrentMatch(currentMatch + 1);
              textRef.current.setSelectionRange(
                  matches[currentMatch + 1].index,
                  matches[currentMatch + 1].index + searchTerm.length
              );
              textRef.current.focus();
          }
      };
  
      // Function to go to the previous match
      const goToPreviousMatch = () => {
          if (currentMatch > 0) {
              setCurrentMatch(currentMatch - 1);
              textRef.current.setSelectionRange(
                  matches[currentMatch - 1].index,
                  matches[currentMatch - 1].index + searchTerm.length
              );
              textRef.current.focus();
          }
      };
  return (
    <nav className={`navbar navbar-expand-lg ${props.mode === 'dark' ? 'navbar-dark bg-dark' : 'navbar-light bg-light'}`}>
      <div className="container-fluid">
        <a className="navbar-brand" href="/">TEXTUTILS</a>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <NavLink className="nav-link" to="/home" end>Home</NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/about" end>{props.about}</NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/login" end>Login</NavLink>
            </li>

          </ul>
          <div className="search-sidebar">
                    <input
                        type="text"
                        className="form-control"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        placeholder="Search..."
                    />
                    <button className="btn btn-primary my-2" onClick={handleSearch}>Search</button>
                    {/* Search result count */}
                    {searchCount > 0 && (
                        <p className="my-2">
                            🔍 Found <strong>{searchCount}</strong> match(es) for "<strong>{searchTerm}</strong>"
                        </p>
                    )}

                    {/* Navigation buttons for multiple matches */}
                    <button className="btn btn-primary" onClick={goToPreviousMatch} disabled={currentMatch === 0}>Previous</button>
                    <button className="btn btn-primary mx-2" onClick={goToNextMatch} disabled={currentMatch === matches.length - 1}>Next</button>

                
                </div>
       

        </div>
        <div className={`form-check form-switch text-${props.mode === 'light' ? 'dark' : 'light'} mx-2`}>
          <input className="form-check-input" type="checkbox" role="switch" onClick={props.toggleMode} id="switchCheckDefault" />
          <label className="form-check-label" htmlFor="switchCheckDefault">Enable Dark mode</label>
        </div>
      </div>
    </nav>
  );
}

Navbar.propTypes = {
  mode: propTypes.string.isRequired,
  toggleMode: propTypes.func.isRequired,
};
